import { books } from "../../data/books";
import { useParams } from "react-router-dom";





const Authors=()=>{
    const { id } = useParams();
    const book = books.find((b) => b.id === +id);

    return(
       
        <img
          src={`/books/${book.image}`}
          alt={book.title}
          
        />
    
    )
}
export default Authors;
